function hideCookieBar(){let e=document.querySelector(".cookie-bar");e.classList.remove("show"),setTimeout((()=>{e.style.display="none"}),500)}window.addEventListener("load",(()=>{if(document.querySelector(".preloader").style.display="none",!localStorage.getItem("cookiesAccepted")&&!localStorage.getItem("cookiesRejected")){let e=document.querySelector(".cookie-bar");e.style.display="flex",setTimeout((()=>{e.classList.add("show")}),10)}})),document.getElementById("acceptCookies").addEventListener("click",(()=>{localStorage.setItem("cookiesAccepted","true"),hideCookieBar()})),document.getElementById("rejectCookies").addEventListener("click",(()=>{localStorage.setItem("cookiesRejected","true"),hideCookieBar()}));