// Cookie Bar Functions
function hideCookieBar() {
    let cookieBar = document.querySelector(".cookie-bar");
    cookieBar.classList.remove("show");
    setTimeout(() => {
        cookieBar.style.display = "none";
    }, 500);
}

// Cookie Bar - Show on page load if not accepted/rejected
window.addEventListener("load", () => {
    // Check if cookies have been accepted or rejected
    if (!localStorage.getItem("cookiesAccepted") && !localStorage.getItem("cookiesRejected")) {
        let cookieBar = document.querySelector(".cookie-bar");
        cookieBar.style.display = "flex";
        setTimeout(() => {
            cookieBar.classList.add("show");
        }, 10);
    }
});

// Accept Cookies Button
document.getElementById("acceptCookies").addEventListener("click", () => {
    localStorage.setItem("cookiesAccepted", "true");
    hideCookieBar();
});

// Reject Cookies Button
document.getElementById("rejectCookies").addEventListener("click", () => {
    localStorage.setItem("cookiesRejected", "true");
    hideCookieBar();
});
